$("html").easeScroll();

document.addEventListener("DOMContentLoaded", function() {
    $(window).load(function() {
        initBarba();
        var controller = new ScrollMagic.Controller();
    });
});

function scrollMagic() {
    controller = new ScrollMagic.Controller();
    scene = new ScrollMagic.Scene();
    var duration = .75;
    var animations = [ {
        y: "+=50",
        scale: 1,
        opacity: 0
    }, {
        height: 0,
        opacity: 0
    }, {
        delay: .75,
        y: "+=50",
        scale: .9,
        opacity: 0
    }, {
        alpha: 0,
        width: 0
    } ];
    $("[animate-fade]").each(function(index) {
        var tl = new TimelineMax();
        tl.from(this, duration, animations[0]);
        scene = new ScrollMagic.Scene({
            triggerElement: this,
            triggerHook: .8,
            reverse: false
        }).setTween(tl).addTo(controller);
    });
    $("[animate-width]").each(function(index) {
        var tl = new TimelineMax();
        tl.from(this, .75, animations[3]);
        scene = new ScrollMagic.Scene({
            triggerElement: this,
            triggerHook: .8,
            reverse: false
        }).setTween(tl).addTo(controller);
    });
    $("[animate-text-roll]").each(function(index) {
        var splitone = new SplitText(this, {
            type: "chars,words, lines"
        }), tl = new TimelineLite({
            delay: 1
        });
        var tl = new TimelineMax();
        tl.staggerFrom(splitone.chars, .8, {
            opacity: 0,
            scale: 0,
            y: 80,
            rotationX: 180,
            transformOrigin: "0% 50% -50",
            ease: Back.easeOut
        }, .01, "+=0");
        scene = new ScrollMagic.Scene({
            triggerElement: this,
            triggerHook: .8,
            reverse: false
        }).setTween(tl).addTo(controller);
    });
    $("[animate-fade-2]").each(function(index) {
        var tl = new TimelineMax();
        tl.from(this, .75, animations[2]);
        scene = new ScrollMagic.Scene({
            triggerElement: this,
            triggerHook: .8,
            reverse: false
        }).setTween(tl).addTo(controller);
    });
    $("[animate-text]").each(function(index) {
        var splitone = new SplitText(this, {
            type: "chars,words, lines"
        }), tl = new TimelineLite({
            delay: 1
        });
        var tl = new TimelineMax();
        tl.staggerFrom(splitone.chars, .5, {
            y: 80,
            opacity: 0,
            ease: Power4.easeOut
        }, .01);
        scene = new ScrollMagic.Scene({
            triggerElement: this,
            triggerHook: .6,
            reverse: false
        }).setTween(tl).addTo(controller);
    });
    $("[animate-text-loop]").each(function(index) {
        var splitone = new SplitText(this, {
            type: "chars,words, lines"
        }), tl = new TimelineLite({
            delay: .5
        });
        var tl = new TimelineMax();
        tl.staggerFrom(splitone.chars, 3, {
            delay: .5,
            y: 80,
            opacity: 0,
            ease: Power4.easeOut,
            repeat: -1
        }, .01);
        scene = new ScrollMagic.Scene({
            triggerElement: this,
            triggerHook: .8,
            reverse: false
        }).setTween(tl).addTo(controller);
    });
    $("[animate-line]").each(function(index) {
        var tl = new TimelineMax();
        tl.from(this, duration, animations[1]);
        var scene = new ScrollMagic.Scene({
            triggerElement: this,
            triggerHook: .8,
            reverse: false
        }).setTween(tl).addTo(controller);
    });
    $("[animate-overlay]").each(function(index) {
        var tl = new TimelineMax();
        tl.fromTo(this, 1, {
            skewX: 30,
            scale: 1.5
        }, {
            delay: 1,
            skewX: 0,
            xPercent: 100,
            transformOrigin: "0% 100%",
            repeatDelay: 1,
            ease: Power2.easeOut
        });
        scene = new ScrollMagic.Scene({
            triggerElement: this,
            triggerHook: .6,
            reverse: false
        }).setTween(tl).addTo(controller);
    });
    $("[animate-parallax]").each(function(index) {
        var pColorScene = new ScrollMagic.Scene({
            triggerElement: "#main-content",
            triggerHook: .5,
            duration: "400%"
        }).setTween(TweenMax.from(this, .5, {
            y: "-200%",
            ease: Power0.easeNone
        })).addTo(controller);
    });
}

